import React, { useEffect, useState } from "react";
import {
  collection,
  getDocs,
  addDoc,
  query,
  where,
} from "firebase/firestore";
import { db } from "../firebase";
import Swal from "sweetalert2";

const PartnerEarnings = () => {
  const [partners, setPartners] = useState([]);
  const [orders, setOrders] = useState([]);
  const [estimates, setEstimates] = useState([]);
  const [selectedPartner, setSelectedPartner] = useState("");
  const [earningsInput, setEarningsInput] = useState({});

  // ðŸ”¹ Fetch all data
  useEffect(() => {
    const fetchData = async () => {
      const partnersSnap = await getDocs(
        query(collection(db, "partners"), where("approved", "==", true))
      );
      const ordersSnap = await getDocs(collection(db, "orders"));
      const estimatesSnap = await getDocs(collection(db, "estimates"));

      setPartners(partnersSnap.docs.map((d) => ({ id: d.id, ...d.data() })));
      setOrders(ordersSnap.docs.map((d) => ({ id: d.id, ...d.data() })));
      setEstimates(estimatesSnap.docs.map((d) => ({ id: d.id, ...d.data() })));
    };
    fetchData();
  }, []);

  const selectedPartnerData = partners.find((p) => p.id === selectedPartner);

  // âœ… Filter completed orders for selected partner
  const partnerOrders =
    selectedPartnerData && orders.length > 0
      ? orders.filter(
          (order) =>
            order.partnerId === selectedPartnerData.id &&
            order.status === "completed"
        )
      : [];

  // âœ… Helper to merge services + estimates for a given order
  const getCombinedServices = (orderId, partnerId) => {
    const order = orders.find((o) => o.orderId === orderId);
    const relatedEstimates = estimates.filter(
      (e) =>
        e.orderId === orderId &&
        e.partnerId === partnerId &&
        e.services?.some((s) => s.status === "approved")
    );

    const baseServices =
      order?.items?.map((i) => ({
        name: i.name || "Unnamed Service",
        price: i.price || 0,
      })) || [];

    const estimateServices = relatedEstimates.flatMap((est) =>
      est.services
        ?.filter((s) => s.status === "approved")
        .map((s) => ({
          name: s.subCategory || s.description || "Estimate Service",
          price:
            s.totalAmount || s.labour_charge || s.service_charge || 0,
        }))
    );

    return [...baseServices, ...estimateServices];
  };

  const handleEarningChange = (orderId, value) => {
    setEarningsInput((prev) => ({
      ...prev,
      [orderId]: value,
    }));
  };

  const processPayout = async (order) => {
    const value = parseFloat(earningsInput[order.id]);
    if (!value || isNaN(value)) {
      Swal.fire("âš ï¸ Please enter a valid earning amount!");
      return;
    }

    const combinedServices = getCombinedServices(
      order.orderId,
      selectedPartner
    );

    await addDoc(collection(db, "payouts"), {
      partnerId: selectedPartner,
      partnerName: selectedPartnerData?.name || "N/A",
      partnerPhone: selectedPartnerData?.phone || "N/A",
      orderId: order.orderId,
      serviceDetails: combinedServices.map(
        (s) => `${s.name} â€” â‚¹${s.price}`
      ),
      totalCustomerAmount: order.totalAmount || 0,
      partnerEarning: value,
      createdAt: new Date(),
      status: "processed",
    });

    Swal.fire("âœ… Payout Recorded", `Order ID: ${order.orderId}`, "success");
    setEarningsInput((prev) => ({ ...prev, [order.id]: "" }));
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Partner Earnings Manager</h2>

      {/* Partner selector dropdown */}
      <div className="mb-6">
        <label className="font-semibold mr-2">Select Partner:</label>
        <select
          className="border p-2 rounded"
          value={selectedPartner}
          onChange={(e) => setSelectedPartner(e.target.value)}
        >
          <option value="">-- Select Partner --</option>
          {partners.map((p) => (
            <option key={p.id} value={p.id}>
              {p.name} ({p.phone})
            </option>
          ))}
        </select>
      </div>

      {/* If partner selected */}
      {selectedPartner && (
        <>
          <h3 className="text-lg font-semibold mb-2">
            {selectedPartnerData?.name}'s Completed Orders
          </h3>

          {partnerOrders.length > 0 ? (
            <table className="w-full border-collapse border border-gray-300 text-left">
              <thead className="bg-gray-100">
                <tr>
                  <th className="border p-2">Order ID</th>
                  <th className="border p-2">Services + Estimates</th>
                  <th className="border p-2">Total Paid (Customer)</th>
                  <th className="border p-2">Earning (â‚¹)</th>
                  <th className="border p-2">Action</th>
                </tr>
              </thead>
              <tbody>
                {partnerOrders.map((order) => {
                  const combinedServices = getCombinedServices(
                    order.orderId,
                    selectedPartner
                  );
                  const totalAmt =
                    order.totalAmount ||
                    combinedServices.reduce(
                      (sum, s) => sum + (s.price || 0),
                      0
                    );

                  return (
                    <tr key={order.id}>
                      <td className="border p-2">{order.orderId}</td>
                      <td className="border p-2">
                        {combinedServices.length > 0 ? (
                          combinedServices.map((s, i) => (
                            <div key={i}>
                              {s.name} â€” â‚¹{s.price}
                            </div>
                          ))
                        ) : (
                          <div>No Services Found</div>
                        )}
                      </td>
                      <td className="border p-2">â‚¹{totalAmt}</td>
                      <td className="border p-2">
                        <input
                          type="number"
                          value={earningsInput[order.id] || ""}
                          onChange={(e) =>
                            handleEarningChange(order.id, e.target.value)
                          }
                          placeholder="Enter â‚¹"
                          className="border rounded p-1 w-24"
                        />
                      </td>
                      <td className="border p-2">
                        <button
                          onClick={() => processPayout(order)}
                          className="bg-green-500 hover:bg-green-600 text-white px-3 py-1 rounded"
                        >
                          Process
                        </button>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          ) : (
            <p className="text-gray-500 mt-3">
              No completed orders found for this partner.
            </p>
          )}
        </>
      )}
    </div>
  );
};

export default PartnerEarnings;
