/* --------------------------------------------------------
   üîî OrderNotifier.js  
   Global listener for new orders ‚Üí Telegram + WhatsApp
-------------------------------------------------------- */

import { collection, onSnapshot } from "firebase/firestore";
import { db } from "../firebase";
import { sendTelegramUpdate, sendWhatsAppUpdate } from "./notifications";

let listenerStarted = false;

export const startOrderNotificationListener = () => {
  if (listenerStarted) {
    console.log("‚ö†Ô∏è Listener already running ‚Äî skipping duplicate.");
    return;
  }
  listenerStarted = true;

  console.log("üöÄ Global Order Listener Started");

  let notifiedOrders = new Set(
    JSON.parse(localStorage.getItem("notifiedOrders") || "[]")
  );

  onSnapshot(collection(db, "orders"), async (snapshot) => {
    const ordersList = snapshot.docs.map((doc) => ({
      id: doc.id,
      ...doc.data(),
    }));

    ordersList.sort(
      (a, b) => (b.timestamp?.seconds || 0) - (a.timestamp?.seconds || 0)
    );

    // detect only new booked orders
    const newOrders = ordersList.filter(
      (order) => !notifiedOrders.has(order.id) && order.status === "booked"
    );

    for (const order of newOrders) {
      const message = `
üì¶ *New Order Booked!*

üßæ *Order ID:* ${order.orderId}
üë§ *Customer:* ${order.customerName}
üìû *Phone:* ${order.customerPhone}
üóìÔ∏è *Date:* ${order.date}
üí∞ *Total:* ‚Çπ${order.totalAmount || 0}
      `;

      // Telegram Admin Alert
      await sendTelegramUpdate(message);

      // WhatsApp to customer
      if (order.customerPhone) {
        const phone = order.customerPhone.replace(/\D/g, "");
        await sendWhatsAppUpdate(
          phone,
          `Your order #${order.orderId} is confirmed! Total: ‚Çπ${order.totalAmount}`
        );
      }

      notifiedOrders.add(order.id);
    }

    localStorage.setItem("notifiedOrders", JSON.stringify([...notifiedOrders]));
  });
};
