import React, { useEffect, useState } from "react";
import {
  collection,
  onSnapshot,
  query,
  where,
  doc,
  setDoc,
} from "firebase/firestore";
import { db } from "../firebase";
import Swal from "sweetalert2";

const PartnerEarnings = () => {
  const [partners, setPartners] = useState([]);
  const [orders, setOrders] = useState([]);
  const [expandedPartner, setExpandedPartner] = useState(null);
  const [editedEarnings, setEditedEarnings] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // ✅ Listen to approved partners
    const partnerQuery = query(collection(db, "partners"), where("approved", "==", true));
    const unsubPartners = onSnapshot(partnerQuery, (snap) => {
      setPartners(snap.docs.map((d) => ({ id: d.id, ...d.data() })));
    });

    // ✅ Listen to completed orders
    const orderQuery = query(collection(db, "orders"), where("status", "==", "completed"));
    const unsubOrders = onSnapshot(orderQuery, (snap) => {
      setOrders(snap.docs.map((d) => ({ id: d.id, ...d.data() })));
      setLoading(false);
    });

    return () => {
      unsubPartners();
      unsubOrders();
    };
  }, []);

  const calculateEarnings = (partnerId) => {
    const partnerOrders = orders.filter((o) => o.partnerId === partnerId);
    return partnerOrders.reduce((sum, o) => sum + (editedEarnings[o.id] ? parseFloat(editedEarnings[o.id]) || 0 : 0), 0);
  };

  const handleEarningChange = (orderId, value) => {
    setEditedEarnings((prev) => ({ ...prev, [orderId]: value }));
  };

  const savePayout = async (order) => {
    const amount = parseFloat(editedEarnings[order.id]);
    if (isNaN(amount)) {
      Swal.fire("❌ Invalid amount", "Please enter a valid number.", "error");
      return;
    }

    try {
      const serviceDetails = order.services
        ?.map((s) => `${s.name} — ₹${s.price}`)
        .join(", ") || "No service data";

      const totalPrice =
        order.services?.reduce((sum, s) => sum + (s.price || 0), 0) || 0;

      // ✅ Save only in payouts/{partnerId}/orders/{orderId}
      await setDoc(doc(db, "payouts", order.partnerId, "orders", order.id), {
        orderId: order.id,
        partnerId: order.partnerId,
        services: order.services || [],
        serviceSummary: serviceDetails,
        totalServicePrice: totalPrice,
        partnerEarning: amount,
        date: new Date(),
      });

      Swal.fire("✅ Saved!", "Payout recorded successfully.", "success");
    } catch (error) {
      console.error(error);
      Swal.fire("❌ Error", "Failed to save payout.", "error");
    }
  };

  if (loading) return <p>Loading data...</p>;
  if (partners.length === 0) return <p>No approved partners found.</p>;

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Partner Earnings</h2>
      <table className="w-full border-collapse border border-gray-300 text-left">
        <thead className="bg-gray-100">
          <tr>
            <th className="border p-2">Partner Name</th>
            <th className="border p-2">Phone</th>
            <th className="border p-2">Completed Orders</th>
            <th className="border p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {partners.map((partner) => {
            const partnerOrders = orders.filter((o) => o.partnerId === partner.id);
            return (
              <React.Fragment key={partner.id}>
                <tr>
                  <td className="border p-2">{partner.name}</td>
                  <td className="border p-2">{partner.phone}</td>
                  <td className="border p-2">{partnerOrders.length}</td>
                  <td className="border p-2">
                    <button
                      onClick={() =>
                        setExpandedPartner(expandedPartner === partner.id ? null : partner.id)
                      }
                      className="bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded"
                    >
                      {expandedPartner === partner.id ? "Hide" : "View"}
                    </button>
                  </td>
                </tr>

                {expandedPartner === partner.id && (
                  <tr>
                    <td colSpan={4} className="border p-4 bg-gray-50">
                      <h4 className="font-semibold mb-2">Completed Orders</h4>
                      {partnerOrders.length === 0 ? (
                        <p>No completed orders yet.</p>
                      ) : (
                        <table className="w-full border border-gray-300 text-sm">
                          <thead className="bg-gray-100">
                            <tr>
                              <th className="border p-2">Order ID</th>
                              <th className="border p-2">Service(s)</th>
                              <th className="border p-2">Earning (₹)</th>
                              <th className="border p-2">Action</th>
                            </tr>
                          </thead>
                          <tbody>
                            {partnerOrders.map((order) => (
                              <tr key={order.id}>
                                <td className="border p-2">{order.id}</td>
                                <td className="border p-2">
                                  {order.services?.map((s, i) => (
                                    <div key={i}>
                                      {s.name} — ₹{s.price}
                                    </div>
                                  )) || "No service data"}
                                </td>
                                <td className="border p-2">
                                  <input
                                    type="number"
                                    placeholder="Enter amount"
                                    onChange={(e) =>
                                      handleEarningChange(order.id, e.target.value)
                                    }
                                    className="border rounded p-1 w-24"
                                  />
                                </td>
                                <td className="border p-2">
                                  <button
                                    onClick={() => savePayout(order)}
                                    className="bg-green-500 hover:bg-green-600 text-white px-3 py-1 rounded"
                                  >
                                    Save
                                  </button>
                                </td>
                              </tr>
                            ))}
                          </tbody>
                        </table>
                      )}
                    </td>
                  </tr>
                )}
              </React.Fragment>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default PartnerEarnings;
