/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useState } from "react";
import { collection, getDocs, updateDoc, doc, query, where, orderBy } from "firebase/firestore";
import { db } from "../firebase";
import Swal from "sweetalert2";

const SlotManager = () => {
  const [slots, setSlots] = useState([]);
  const [loading, setLoading] = useState(true);
  const dayLabels = ["Today", "Tomorrow", "Day After Tomorrow"];

  // ðŸ”¹ Load all slots
  const loadSlots = async () => {
    setLoading(true);
    const allSlots = [];
    for (const label of dayLabels) {
      const q = query(
        collection(db, "time_slots"),
        where("label", "==", label),
        orderBy("time")
      );
      const snap = await getDocs(q);
      snap.forEach((docSnap) => {
        allSlots.push({ id: docSnap.id, ...docSnap.data() });
      });
    }
    setSlots(allSlots);
    setLoading(false);
  };

  useEffect(() => {
    loadSlots();
  }, [loadSlots]);

  // ðŸ”¹ Toggle single slot availability
  const toggleSlot = async (slot) => {
    await updateDoc(doc(db, "time_slots", slot.id), {
      available: !slot.available
    });
    // Refresh manually after change
    loadSlots();
  };

  // ðŸ”¹ Toggle all slots for a day
  const toggleAllForDay = async (label) => {
    const confirm = await Swal.fire({
      title: `Disable all slots for ${label}?`,
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "Yes, disable",
    });
    if (!confirm.isConfirmed) return;

    const q = query(collection(db, "time_slots"), where("label", "==", label));
    const snap = await getDocs(q);
    const updates = snap.docs.map((d) =>
      updateDoc(doc(db, "time_slots", d.id), { available: false })
    );
    await Promise.all(updates);
    loadSlots(); // refresh
    Swal.fire("Success", `All slots for ${label} disabled`, "success");
  };

  if (loading) return <p>Loading slots...</p>;

  return (
    <div className="page-container">
      <h2>Slot Manager</h2>
      <div style={{ display: "flex", gap: "10px", marginBottom: "20px" }}>
        {dayLabels.map((label) => (
          <button key={label} onClick={() => toggleAllForDay(label)}>
            Disable All {label} Slots
          </button>
        ))}
      </div>

      <table>
        <thead>
          <tr>
            <th>Day</th>
            <th>Time</th>
            <th>Available</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {slots.length === 0 ? (
            <tr>
              <td colSpan={4} style={{ textAlign: "center" }}>
                No slots found
              </td>
            </tr>
          ) : (
            slots.map((slot) => (
              <tr key={slot.id}>
                <td>{slot.label}</td>
                <td>{slot.time}</td>
                <td style={{ color: slot.available ? "green" : "red" }}>
                  {slot.available ? "Yes" : "No"}
                </td>
                <td>
                  <button onClick={() => toggleSlot(slot)}>
                    {slot.available ? "Disable" : "Enable"}
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default SlotManager;
