import React, { useEffect, useState } from "react";
import {
  collection,
  onSnapshot,
  updateDoc,
  doc,
  query,
  where,
} from "firebase/firestore";
import { db } from "../firebase";
import Swal from "sweetalert2";

const PartnerEarnings = () => {
  const [partners, setPartners] = useState([]);
  const [orders, setOrders] = useState([]);
  const [expandedPartner, setExpandedPartner] = useState(null);
  const [editedEarnings, setEditedEarnings] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // ✅ Real-time listener for approved partners
    const partnerQuery = query(collection(db, "partners"), where("approved", "==", true));
    const unsubscribePartners = onSnapshot(partnerQuery, (snapshot) => {
      setPartners(snapshot.docs.map((d) => ({ id: d.id, ...d.data() })));
    });

    // ✅ Real-time listener for completed orders
    const orderQuery = query(collection(db, "orders"), where("status", "==", "completed"));
    const unsubscribeOrders = onSnapshot(orderQuery, (snapshot) => {
      setOrders(snapshot.docs.map((d) => ({ id: d.id, ...d.data() })));
      setLoading(false);
    });

    return () => {
      unsubscribePartners();
      unsubscribeOrders();
    };
  }, []);

  const calculateEarnings = (partnerId) => {
    const completedOrders = orders.filter((order) => order.partnerId === partnerId);
    const totalEarnings = completedOrders.reduce(
      (sum, order) => sum + (order.partnerEarning || 0),
      0
    );
    return totalEarnings;
  };

  const handlePayout = async (partner) => {
    Swal.fire({
      title: `Mark payout for ${partner.name}?`,
      text: `Earnings till now: ₹${calculateEarnings(partner.id)}`,
      icon: "question",
      showCancelButton: true,
      confirmButtonText: "Confirm",
    }).then(async (res) => {
      if (res.isConfirmed) {
        await updateDoc(doc(db, "partners", partner.id), {
          lastPayout: new Date(),
          totalPaid: calculateEarnings(partner.id),
        });
        Swal.fire("✅ Payout recorded!", "", "success");
      }
    });
  };

  const handleEarningChange = (orderId, value) => {
    setEditedEarnings((prev) => ({
      ...prev,
      [orderId]: value,
    }));
  };

  const saveEarning = async (orderId) => {
    const newAmount = parseFloat(editedEarnings[orderId]);
    if (isNaN(newAmount)) {
      Swal.fire("❌ Invalid amount", "Please enter a valid number.", "error");
      return;
    }

    await updateDoc(doc(db, "orders", orderId), { partnerEarning: newAmount });
    Swal.fire("✅ Updated!", "Partner earning updated successfully.", "success");
  };

  if (loading) return <p>Loading partner earnings...</p>;

  if (partners.length === 0) {
    return (
      <p style={{ textAlign: "center", marginTop: "20px" }}>
        No approved partners found.
      </p>
    );
  }

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Partner Earnings</h2>

      <table className="w-full border-collapse border border-gray-300 text-left">
        <thead className="bg-gray-100">
          <tr>
            <th className="border p-2">Partner Name</th>
            <th className="border p-2">Phone</th>
            <th className="border p-2">Completed Orders</th>
            <th className="border p-2">Total Earnings</th>
            <th className="border p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {partners.map((partner) => {
            const completedOrders = orders.filter(
              (o) => o.partnerId === partner.id
            );
            const earnings = calculateEarnings(partner.id);
            return (
              <React.Fragment key={partner.id}>
                <tr>
                  <td className="border p-2">{partner.name}</td>
                  <td className="border p-2">{partner.phone}</td>
                  <td className="border p-2">{completedOrders.length}</td>
                  <td className="border p-2">₹{earnings}</td>
                  <td className="border p-2">
                    <button
                      onClick={() =>
                        setExpandedPartner(
                          expandedPartner === partner.id ? null : partner.id
                        )
                      }
                      className="bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded mr-2"
                    >
                      {expandedPartner === partner.id ? "Hide" : "View"}
                    </button>
                    <button
                      onClick={() => handlePayout(partner)}
                      className="bg-green-500 hover:bg-green-600 text-white px-3 py-1 rounded"
                    >
                      Mark Paid
                    </button>
                  </td>
                </tr>

                {expandedPartner === partner.id && (
                  <tr>
                    <td colSpan={5} className="border p-4 bg-gray-50">
                      <h4 className="font-semibold mb-2">Completed Orders</h4>
                      {completedOrders.length === 0 ? (
                        <p>No completed orders for this partner.</p>
                      ) : (
                        <table className="w-full border border-gray-300 text-sm">
                          <thead className="bg-gray-100">
                            <tr>
                              <th className="border p-2">Order ID</th>
                              <th className="border p-2">Service</th>
                              <th className="border p-2">Earning (₹)</th>
                              <th className="border p-2">Action</th>
                            </tr>
                          </thead>
                          <tbody>
                            {completedOrders.map((order) => (
                              <tr key={order.id}>
                                <td className="border p-2">{order.id}</td>
                                <td className="border p-2">
                                  {order.serviceName || "—"}
                                </td>
                                <td className="border p-2">
                                  <input
                                    type="number"
                                    defaultValue={order.partnerEarning || ""}
                                    onChange={(e) =>
                                      handleEarningChange(
                                        order.id,
                                        e.target.value
                                      )
                                    }
                                    className="border rounded p-1 w-24"
                                  />
                                </td>
                                <td className="border p-2">
                                  <button
                                    onClick={() => saveEarning(order.id)}
                                    className="bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded"
                                  >
                                    Save
                                  </button>
                                </td>
                              </tr>
                            ))}
                          </tbody>
                        </table>
                      )}
                    </td>
                  </tr>
                )}
              </React.Fragment>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default PartnerEarnings;
