import React, { useEffect, useState } from "react";
import {
  collection,
  query,
  where,
  getDocs,
  doc,
  setDoc,
} from "firebase/firestore";
import { db } from "../firebase";

const PartnerEarnings = () => {
  const [partners, setPartners] = useState([]);
  const [selectedPartner, setSelectedPartner] = useState("");
  const [orders, setOrders] = useState([]);
  const [estimates, setEstimates] = useState([]);
  const [earnings, setEarnings] = useState({});
  const [loading, setLoading] = useState(false);
  const [debugStatuses, setDebugStatuses] = useState([]);

  // ‚úÖ Fetch approved partners
  useEffect(() => {
    const fetchPartners = async () => {
      const q = query(collection(db, "partners"), where("approved", "==", true));
      const snapshot = await getDocs(q);
      const list = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setPartners(list);
    };
    fetchPartners();
  }, []);

  // ‚úÖ Fetch orders & estimates for selected partner
  useEffect(() => {
    const fetchData = async () => {
      if (!selectedPartner) return;
      setLoading(true);

      try {
        // üîπ Fetch all orders where handymanAssigned = selected partner
        const ordersRef = collection(db, "orders");
        const q = query(ordersRef, where("handymanAssigned", "==", selectedPartner));
        const orderSnapshot = await getDocs(q);
        const orderList = orderSnapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }));

        // ‚úÖ Collect all statuses to check what‚Äôs coming from Firestore
        const allStatuses = orderList.map((o) => o.status);
        setDebugStatuses(allStatuses);

        // üîπ Filter only completed / closed
        const filteredOrders = orderList.filter((order) => {
          const s = order.status?.toLowerCase() || "";
          return s === "completed" || s === "closed";
        });

        // üîπ Fetch estimates related to this partner
        const estimatesRef = collection(db, "estimates");
        const estSnap = await getDocs(
          query(estimatesRef, where("partnerId", "==", selectedPartner))
        );
        const estList = estSnap.docs
          .map((doc) => ({ id: doc.id, ...doc.data() }))
          .filter((e) => e.status?.toLowerCase() === "approved");

        setOrders(filteredOrders);
        setEstimates(estList);
      } catch (err) {
        console.error("Error fetching data:", err);
      }
      setLoading(false);
    };
    fetchData();
  }, [selectedPartner]);

  // ‚úÖ Handle payout processing
  const handleProcessPayout = async (orderId) => {
    const partnerId = selectedPartner;
    const amount = earnings[orderId];
    if (!amount) return alert("Please enter an amount!");

    const payoutRef = doc(collection(db, "payouts"), `${partnerId}_${orderId}`);
    await setDoc(payoutRef, {
      partnerId,
      orderId,
      amount: parseFloat(amount),
      timestamp: new Date(),
    });

    alert("‚úÖ Payout processed successfully!");
    setEarnings((prev) => ({ ...prev, [orderId]: "" }));
  };

  // ‚úÖ Get services + approved estimates combined
  const getServiceAndEstimates = (orderId) => {
    const order = orders.find((o) => o.orderId === orderId);
    const ests = estimates.filter((e) => e.orderId === orderId);

    const serviceItems =
      order?.items?.map(
        (item) => `${item.name} (‚Çπ${item.price || 0})`
      ) || [];

    const estimateItems = ests.flatMap((e) =>
      e.services.map(
        (s) =>
          `${s.subCategory || s.description} (‚Çπ${s.totalAmount || s.labour_charge + s.service_charge || 0})`
      )
    );

    return [...serviceItems, ...estimateItems];
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-semibold mb-4 text-blue-600">
        Partner Earnings Management
      </h2>

      {/* üîπ Partner Dropdown */}
      <div className="mb-6">
        <label className="font-medium">Select Partner:</label>
        <select
          value={selectedPartner}
          onChange={(e) => setSelectedPartner(e.target.value)}
          className="ml-3 p-2 border rounded-md"
        >
          <option value="">-- Select Partner --</option>
          {partners.map((partner) => (
            <option key={partner.id} value={partner.id}>
              {partner.name} ({partner.phone})
            </option>
          ))}
        </select>
      </div>

      {loading ? (
        <p>Loading data...</p>
      ) : selectedPartner ? (
        orders.length > 0 ? (
          <div className="overflow-x-auto bg-white shadow rounded-lg">
            <h3 className="text-lg font-semibold mb-2 text-green-600 p-2">
              ‚úÖ Showing Completed / Closed Orders
            </h3>
            <table className="min-w-full border-collapse border border-gray-300 text-sm">
              <thead className="bg-gray-100">
                <tr>
                  <th className="border p-2">Order ID</th>
                  <th className="border p-2">Services + Estimates</th>
                  <th className="border p-2">Total Amount (‚Çπ)</th>
                  <th className="border p-2">Partner Earning (‚Çπ)</th>
                  <th className="border p-2">Action</th>
                </tr>
              </thead>
              <tbody>
                {orders.map((order) => {
                  const combined = getServiceAndEstimates(order.orderId);
                  return (
                    <tr key={order.id}>
                      <td className="border p-2 text-center">
                        {order.orderId || order.id}
                      </td>
                      <td className="border p-2">
                        {combined.length > 0
                          ? combined.join(", ")
                          : "No services or estimates"}
                      </td>
                      <td className="border p-2 text-center">
                        ‚Çπ{order.totalAmount || 0}
                      </td>
                      <td className="border p-2 text-center">
                        <input
                          type="number"
                          placeholder="Enter amount"
                          value={earnings[order.orderId] || ""}
                          onChange={(e) =>
                            setEarnings((prev) => ({
                              ...prev,
                              [order.orderId]: e.target.value,
                            }))
                          }
                          className="p-1 border rounded-md w-24 text-center"
                        />
                      </td>
                      <td className="border p-2 text-center">
                        <button
                          onClick={() => handleProcessPayout(order.orderId)}
                          className="bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded-md"
                        >
                          Process
                        </button>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        ) : (
          <>
            <p className="text-gray-600">
              ‚ö†Ô∏è No completed/closed orders found for this partner.
            </p>
            <p className="text-xs text-gray-400 mt-2">
              (Debug: Found order statuses ‚Üí {debugStatuses.join(", ") || "none"})
            </p>
          </>
        )
      ) : (
        <p className="text-gray-600">Please select a partner to view earnings.</p>
      )}
    </div>
  );
};

export default PartnerEarnings;
