// src/pages/PartnerEarnings.js
import React, { useEffect, useState } from "react";
import {
  collection,
  onSnapshot,
  updateDoc,
  doc,
  query,
  where
} from "firebase/firestore";
import { db } from "../firebase";
import Swal from "sweetalert2";

const PartnerEarnings = () => {
  const [partners, setPartners] = useState([]);
  const [orders, setOrders] = useState([]);
  const [editingAmounts, setEditingAmounts] = useState({});

  // üîπ Real-time fetch partners
  useEffect(() => {
    const unsubPartners = onSnapshot(
      query(collection(db, "partners"), where("status", "==", "approved")),
      (snapshot) => {
        setPartners(snapshot.docs.map((d) => ({ id: d.id, ...d.data() })));
      }
    );

    // üîπ Real-time fetch completed orders
    const unsubOrders = onSnapshot(
      query(collection(db, "orders"), where("status", "==", "completed")),
      (snapshot) => {
        setOrders(snapshot.docs.map((d) => ({ id: d.id, ...d.data() })));
      }
    );

    return () => {
      unsubPartners();
      unsubOrders();
    };
  }, []);

  // üîπ Calculate partner earnings
  const calculateEarnings = (partnerId) => {
    const partnerOrders = orders.filter((o) => o.partnerId === partnerId);
    return partnerOrders.reduce(
      (sum, o) => sum + (o.partnerEarning ? Number(o.partnerEarning) : 0),
      0
    );
  };

  // üîπ Update earning per order
  const saveEarning = async (orderId, newAmount) => {
    try {
      await updateDoc(doc(db, "orders", orderId), {
        partnerEarning: Number(newAmount),
      });
      Swal.fire("‚úÖ Updated!", "Partner earning saved.", "success");
    } catch (err) {
      console.error(err);
      Swal.fire("‚ùå Error", "Failed to update earning", "error");
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Partner Earnings</h2>

      {partners.length === 0 ? (
        <p>No approved partners found.</p>
      ) : (
        partners.map((partner) => {
          const partnerOrders = orders.filter(
            (o) => o.partnerId === partner.id
          );
          const totalEarnings = calculateEarnings(partner.id);

          return (
            <div
              key={partner.id}
              className="border rounded-lg mb-6 p-4 shadow-sm bg-white"
            >
              <div className="flex justify-between items-center mb-2">
                <h3 className="text-lg font-semibold">
                  {partner.name} ({partner.phone})
                </h3>
                <span className="font-bold text-green-600">
                  Total: ‚Çπ{totalEarnings}
                </span>
              </div>

              {partnerOrders.length === 0 ? (
                <p className="text-gray-500 text-sm">No completed orders.</p>
              ) : (
                <table className="w-full border-collapse border border-gray-300 text-left text-sm">
                  <thead className="bg-gray-100">
                    <tr>
                      <th className="border p-2">Order ID</th>
                      <th className="border p-2">Date</th>
                      <th className="border p-2">Earning (‚Çπ)</th>
                      <th className="border p-2">Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {partnerOrders.map((order) => (
                      <tr key={order.id}>
                        <td className="border p-2">{order.orderId}</td>
                        <td className="border p-2">
                          {order.date || "N/A"}
                        </td>
                        <td className="border p-2">
                          <input
                            type="number"
                            value={
                              editingAmounts[order.id] ??
                              order.partnerEarning ??
                              ""
                            }
                            onChange={(e) =>
                              setEditingAmounts((prev) => ({
                                ...prev,
                                [order.id]: e.target.value,
                              }))
                            }
                            className="border px-2 py-1 w-24 rounded"
                          />
                        </td>
                        <td className="border p-2">
                          <button
                            onClick={() =>
                              saveEarning(
                                order.id,
                                editingAmounts[order.id] ??
                                  order.partnerEarning ??
                                  0
                              )
                            }
                            className="bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded"
                          >
                            Save
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </div>
          );
        })
      )}
    </div>
  );
};

export default PartnerEarnings;
