import React, { useEffect, useState } from "react";
import {
  collection,
  getDocs,
  updateDoc,
  doc,
  addDoc,
  query,
  where,
} from "firebase/firestore";
import { db } from "../firebase";
import Swal from "sweetalert2";

const PartnerEarnings = () => {
  const [partners, setPartners] = useState([]);
  const [orders, setOrders] = useState([]);
  const [earningsInput, setEarningsInput] = useState({}); // partnerId → amount

  useEffect(() => {
    const fetchData = async () => {
      const partnersSnap = await getDocs(
        query(collection(db, "partners"), where("approved", "==", true))
      );
      const ordersSnap = await getDocs(collection(db, "orders"));
      setPartners(partnersSnap.docs.map((d) => ({ id: d.id, ...d.data() })));
      setOrders(ordersSnap.docs.map((d) => ({ id: d.id, ...d.data() })));
    };
    fetchData();
  }, []);

  const handleEarningChange = (partnerId, orderId, value) => {
    setEarningsInput((prev) => ({
      ...prev,
      [partnerId]: {
        ...(prev[partnerId] || {}),
        [orderId]: value,
      },
    }));
  };

  const savePayout = async (partner, order) => {
    const earning =
      earningsInput?.[partner.id]?.[order.id] &&
      parseFloat(earningsInput[partner.id][order.id]);

    if (!earning || isNaN(earning)) {
      Swal.fire("Please enter a valid amount!", "", "warning");
      return;
    }

    const serviceDetails = order.items
      ?.map((s) => `${s.name || "Unnamed Service"} — ₹${s.price || 0}`)
      .join(", ") || "No service data";

    // ✅ Add payout entry under a 'payouts' collection
    await addDoc(collection(db, "payouts"), {
      partnerId: partner.id,
      partnerName: partner.name,
      partnerPhone: partner.phone,
      orderId: order.orderId,
      orderDocId: order.id,
      amount: earning,
      serviceDetails,
      date: new Date(),
      status: "processed",
    });

    Swal.fire(
      "✅ Payout Recorded!",
      `Partner: ${partner.name}\nOrder ID: ${order.orderId}\nEarning: ₹${earning}`,
      "success"
    );
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Partner Earnings</h2>

      {partners.map((partner) => {
        const completedOrders = orders.filter(
          (o) => o.partnerId === partner.id && o.status === "completed"
        );

        return (
          <div key={partner.id} className="border rounded-lg p-4 mb-6 shadow">
            <h3 className="text-lg font-semibold mb-2">
              {partner.name} ({partner.phone})
            </h3>

            {completedOrders.length === 0 ? (
              <p className="text-gray-500">No completed orders yet.</p>
            ) : (
              <table className="w-full border-collapse border border-gray-300 text-left">
                <thead className="bg-gray-100">
                  <tr>
                    <th className="border p-2">Order ID</th>
                    <th className="border p-2">Services</th>
                    <th className="border p-2">Total Amount</th>
                    <th className="border p-2">Enter Earning</th>
                    <th className="border p-2">Action</th>
                  </tr>
                </thead>
                <tbody>
                  {completedOrders.map((order) => (
                    <tr key={order.id}>
                      <td className="border p-2">{order.orderId}</td>
                      <td className="border p-2">
                        {order.items?.length ? (
                          order.items.map((s, i) => (
                            <div key={i}>
                              {s.name} — ₹{s.price}
                            </div>
                          ))
                        ) : (
                          <div>No items</div>
                        )}
                      </td>
                      <td className="border p-2">₹{order.totalAmount || 0}</td>
                      <td className="border p-2">
                        <input
                          type="number"
                          placeholder="Enter earning"
                          className="border rounded p-1 w-24"
                          value={earningsInput?.[partner.id]?.[order.id] || ""}
                          onChange={(e) =>
                            handleEarningChange(
                              partner.id,
                              order.id,
                              e.target.value
                            )
                          }
                        />
                      </td>
                      <td className="border p-2">
                        <button
                          onClick={() => savePayout(partner, order)}
                          className="bg-green-500 hover:bg-green-600 text-white px-3 py-1 rounded"
                        >
                          Process
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default PartnerEarnings;
